import ApexCharts from "react-apexcharts";
import { useQuery } from "react-query";
import styled from "styled-components";
import { fetchCoinHistory, fetchCoinInfo, fetchCoinTickers } from "../api";

const Overview = styled.div`
    display: flex;
    justify-content: space-between;
    background-color: rgba(0,0,0,0.5);
    padding: 10px 20px;
    border-radius: 10px;
    margin-top: 10px;
`;

const OverviewCenter = styled(Overview)`
    justify-content: center;
`;

const OverviewItem = styled.div`
    display: flex;
    align-items: center;
    width: 100%;
    span{
        margin-right: 10px;
    }
`;

interface InfoData {
    id: string;
    name: string;
    symbol: string;
    rank: number;
    is_new: boolean;
    is_active: boolean;
    type: string;
    description: string;
    message: string;
    open_source: boolean;
    started_at: string;
    development_status: string;
    hardware_wallet: boolean;
    proof_type: string;
    org_structure: string;
    hash_algorithm: string;
    first_data_at: string;
    last_data_at: string;
}

interface PriceData {
    id: string;
    name: string;
    symbol: string;
    rank: number;
    circulating_supply: number;
    total_supply: number;
    max_supply: number;
    beta_value: number;
    first_data_at: string;
    last_updated: string;
    quotes: {
        USD: {
            ath_date: string;
            ath_price: number;
            market_cap: number;
            market_cap_change_24h: number;
            percent_change_1h: number;
            percent_change_1y: number;
            percent_change_6h: number;
            percent_change_7d: number;
            percent_change_12h: number;
            percent_change_15m: number;
            percent_change_24h: number;
            percent_change_30d: number;
            percent_change_30m: number;
            percent_from_price_ath: number;
            price: number;
            volume_24h: number;
            volume_24h_change_24h: number;
        };
    };
}

interface ChartProps {
    coinId: string,
};

function Price({ coinId }: ChartProps){
    const { isLoading: infoLoading, data: infoData } = useQuery<InfoData>(["info", coinId], () => fetchCoinInfo(coinId));
    const { isLoading: tickersLoading, data: tickersData } = useQuery<PriceData>(["tickers", coinId], () => fetchCoinTickers(coinId), { refetchInterval: 5000, });

    return (
        <div>
            {tickersLoading ? ("Loading Price...") : (
            <>
                <OverviewCenter>
                    <OverviewItem>
                        <span>Price :</span>
                        <span>{tickersData?.quotes.USD.price}</span>
                    </OverviewItem>
                </OverviewCenter>
                <Overview>
                    <OverviewItem>
                        <span>Max Change rate in last 24h :</span>
                        <span>{tickersData?.quotes.USD.market_cap_change_24h}</span>
                    </OverviewItem>
                    <OverviewItem>
                        <span>Change rate (last 1 hours):</span>
                        <span>{tickersData?.quotes.USD.percent_change_1h}</span>
                    </OverviewItem>
                </Overview>
                <Overview>
                    <OverviewItem>
                        <span>Change rate (last 12 hours):</span>
                        <span>{tickersData?.quotes.USD.percent_change_12h}</span>
                    </OverviewItem>
                    <OverviewItem>
                        <span>Change rate (last 24 hours):</span>
                        <span>{tickersData?.quotes.USD.percent_change_24h}</span>
                    </OverviewItem>
                </Overview>
            </>
            )}
        </div>
    );
}

export default Price;
